import React from 'react';
import './App.css';
import Login from "./components/Login";
import TodoTerminal from "./components/TodoTerminal";

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      username: "",
      password:"",
      user:[],
      loginsituation: false,
      products:[],
      givenmoney:""
    }
    this.Login = this.Login.bind(this);
    this.handleBarcodeInput = this.handleBarcodeInput.bind(this);
    this.inputProductCount = this.inputProductCount.bind(this);
  }

  handleUsernameInput = (e) => {
    var user = this.state.user;
    var username = e.target.value;
    user = {username,password:this.state.password};
    this.setState({
      user,
      username
    });
  }

  handlePasswordInput = (e) => {
    var user = this.state.user;
    var password = e.target.value;
    user = {username:this.state.username,password};
    this.setState({
      user,
      password
    });

  }

  defaultSituation = () => {
    var products = [];
    for (let i = 0; i < 6; i++) {
     products[i] = {key:i,barcode:"",count:1};

    }
    this.setState({
      products
    })
  }

  handleBarcodeInput(key,e) {

    var barcode = e.target.value;
    var products =  this.state.products;
    products.map(item=>{
      if(item.key === key) {
        item.barcode = barcode;
      }    
      return item;
    });

    if(products[products.length -1].barcode !== "") {
      products[products.length] = {key:products.length,barcode:"",count:1};
    }   

    this.setState({
     products
    });
  }

  Login() {
    const user = this.state.user;    
    fetch('http://localhost:5000/questions',{
        method: 'POST',
        headers: {
            Accept: 'application/json',
            'Content-Type': 'application/json',
        },
        body: JSON.stringify(this.state.user)
    })
    .then(response => {
      // console.log(response);
      this.setState({
        loginsituation:true
      });
    })
    .catch(error =>{
      // console.log(error);
    })
    this.setState({
      loginsituation:true
    });
    var products = [];
    for (let i = 0; i < 6; i++) {
     products[i] = {key:i,barcode:"222",count:1};

    }
    this.setState({
      products
    })
  }

  inputProductCount(key,e) {
    var count = e.target.value;
    var products =  this.state.products;
    products.map(item=>{
      if(item.key === key) {
        item.count = count;
      }    
      return item;
    });
    this.setState({
      products
    })
  }

  inputGivenMoney = (e) => {
    const givenmoney = e.target.value;
    this.setState({
      givenmoney
    });

  }
  transactionDone = () => {
    var products = [];
    var prod = this.state.products;
    for (let i = 0; i < 6; i++) {
     products[i] = {key:i,barcode:"",count:1};
    }
    console.log(products);
    this.setState({
      products
    })
  }

  render() {
    const loginsituation = this.state.loginsituation;
    return (
      <>
      {
        !loginsituation ?
          <Login
          handleUsernameInput = {this.handleUsernameInput}
          handlePasswordInput = {this.handlePasswordInput}
          Login = {this.Login}
        />
        :
        <TodoTerminal
          handleBarcodeInput = {this.handleBarcodeInput}
          products = {this.state.products}
          inputProductCount = {this.inputProductCount}
          inputGivenMoney = {this.inputGivenMoney}
          transactionDone = {this.transactionDone}
        />
      }
      </>


    );
  }
}

export default App;
