import React from 'react';
import './App.css';
import Login from "./components/Login";
import TodoTerminal from "./components/TodoTerminal";

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      username: "",
      password:"",
      user:[],
      loginsituation: false,
      barcodes:[]
    }
    this.Login = this.Login.bind(this);
  }

  handleUsernameInput = (e) => {
    var user = this.state.user;
    var username = e.target.value;
    user = {username,password:this.state.password};
    this.setState({
      user,
      username
    });
  }

  handlePasswordInput = (e) => {
    var user = this.state.user;
    var password = e.target.value;
    user = {username:this.state.username,password};
    this.setState({
      user,
      password
    });

  }

  defaultSituation = () => {
    var barcodes = [];
    for (let i = 0; i < 6; i++) {
     barcode[i] = {key:i,barcode:""};

    }
    this.setState({
      barcodes
    })    
  }

  handleBarcodeInput = (e) => {

    var barcode = e.target.value;
    var key = this.state.barcodes.length;
    var barcodes =  this.state.barcodes;

    barcodes.unshift({
      key,
      barcode
    });

    console.log(e.target);

    this.setState({
     barcodes
    });
  }

  Login() {
    const user = this.state.user;
    console.log(user);  
    console.log(this.state);
    fetch('http://localhost:5000/questions',{
        method: 'POST',
        headers: {
            Accept: 'application/json',
            'Content-Type': 'application/json',
        },
        body: JSON.stringify(this.state.user)
    })
    .then(response => {
      console.log(response);
      this.setState({
        loginsituation:true
      });
    })
    .catch(error =>{
      console.log(error);
    })
    this.setState({
      loginsituation:true
    });
  }



  render() {
    const loginsituation = this.state.loginsituation;
    return (
      <>
      {
        !loginsituation ?
          <Login
          handleUsernameInput = {this.handleUsernameInput}
          handlePasswordInput = {this.handlePasswordInput}
          Login = {this.Login}
        />
        :
        <TodoTerminal
          handleBarcodeInput = {this.handleBarcodeInput}
          barcodes = {this.state.barcodes}
        />
      }
      </>


    );
  }
}

export default App;
