import React from "react";
import TodoTransactionList from "./TodoTransactionList";

class TodoTerminal extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            showTransaction:false,
            transactionlist:[]
        }
    }
    todoTransaction = () => {
        fetch('http://localhost:5000/questions',{
            method: 'POST',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(this.state.user)
        })
        .then(response => {
            console.log(response);
        
        })
        .catch(error =>{
          console.log(error);
        })
    }

    render() {
        const products = this.props.products;
        console.log(products);
        return(
            <div id="todoterminal">
                <header>
                    <span>Cashier info</span>
                    <button onClick = {this.todoTransaction}>Transactions's Manu</button>                
                </header>
                <div>
                    <div id = "productinterminal">
                        <table>
                            <thead>
                                <tr>
                                    <th>Barcode</th>
                                    <th>Product Name</th>
                                    <th>Supplier_Name</th>
                                    <th>Product count</th>
                                    <th>Price</th>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    products.map(item =>{

                                        return (
                                            <tr  key={item.key}>
                                                <td>
                                                    <input
                                                        type="text" 
                                                        value = {item.barcode} 
                                                        onChange={(e)=>{this.props.handleBarcodeInput(item.key,e)}}
                                                    />
                                                </td>
                                                <td></td>
                                                <td></td>
                                                <td>
                                                    <input 
                                                        type="number" 
                                                        min= '1' 
                                                        value = {item.count}
                                                        onChange ={(e)=> {this.props.inputProductCount(item.key,e)}}
                                                    />
                                                </td>
                                                <td></td>
                                            </tr>
                                        );
                                    })
                                }
                                
                            </tbody>
                        </table>
                    </div>
                    <div>
                        <span>Amount</span>
                        <span></span>
                    </div>
                    <div>
                        <div>
                            <span>Given money</span>
                            <input 
                                type="text" 
                                onChange = {this.props.inputGivenMoney}
                            />
                        </div>
                        <div>
                            <span>Change</span>
                            <input type="text"/>
                        </div>
                    </div>
                </div>              
                <button onClick = {this.props.transactionDone} >Done</button>
            </div>
        );
    }
}

export default TodoTerminal;
